package work1;

public class Encapsulation1 {
		int a=15;
		int b=5;
		void A()
		{
			System.out.println(a+b);
		}
		public void B()
		{
			System.out.println(a-b);
		}
		private void C()
		{
			System.out.println(a/b);
		}
		protected void D()
		{
			System.out.println(a*b);
		}
	public static void main(String[] args) {
		// TODO Auto-generated method stub

	}
}
